package _J_01_Simple_Programs;
public class _07_Simple_Java_Programs_ConcatenationOperator_Payilagam {			    // இணைத்தல் ஆபரேட்டர் ***

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	int a = 50, b = 60;
		
	System.out.println("The C value is *** " + a + b);	
	// Here Concatenation Operator works has combining left and right strings****
		
		
	System.out.println("The C Value is ### " + (a + b)); 
	// Here Concatenation Operator works has ADDING left and right VALUE because of BRACKET****
		
	}

}
